import pandas as pd
from sklearn.model_selection import train_test_split
from sklearn.feature_extraction.text import TfidfVectorizer
from sklearn.naive_bayes import MultinomialNB
from sklearn.pipeline import Pipeline
from sklearn.metrics import accuracy_score, precision_score, recall_score, classification_report

"""2.Loading and preparing data"""

data = pd.read_csv("/content/drive/MyDrive/CLG/RISE/Dataset/Project_one.tsv", sep='\t', header=None, names=['label', 'message'])
data['label'] = data['label'].map({'ham': 0, 'spam': 1})
data.head(5)

"""3.Spliting data"""

X_train, X_test, y_train, y_test = train_test_split(data['message'], data['label'], test_size=0.2, random_state=42)

"""4.Building and training model pipeline"""

model = Pipeline([
    ('tfidf', TfidfVectorizer(stop_words='english')),
    ('clf', MultinomialNB())
])
model.fit(X_train, y_train)

"""5.Evaluation of model"""

y_pred = model.predict(X_test)
print("Accuracy:", accuracy_score(y_test, y_pred))
print("Precision:", precision_score(y_test, y_pred))
print("Recall:", recall_score(y_test, y_pred))
print("\nClassification Report:\n", classification_report(y_test, y_pred))

"""6.Function to simulate JSON input/output"""

def predict_spam(json_input: dict) -> dict:
    """
    Accepts JSON input with key 'message', returns JSON with prediction.

    Example:
    json_input = {"message": "You won a lottery!"}
    output = predict_spam(json_input)
    """
    if 'message' not in json_input:
        return {"error": "Missing 'message' in input"}

    message = json_input['message']
    prediction = model.predict([message])[0]
    label = "Spam" if prediction == 1 else "Not Spam"

    return {
        "message": message,
        "prediction": label,
        "label": int(prediction)
    }

"""7.Example usage:"""

if __name__ == "__main__":
    test_json = {"message": "Congratulations! You won a free ticket. Call now!"}
    output = predict_spam(test_json)
    print(output)

